mapper.select:
select * from xt_gnb
where fid is NULL
and dyqx >=  #{dyqx,jdbcType=INTEGER}
and zt = #{zt,jdbcType=INTEGER}
ORDER BY sxh

select * from xt_gnb
where fid is NOT NULL
and dyqx >=  #{dyqx,jdbcType=INTEGER}
and zt = #{zt,jdbcType=INTEGER}
ORDER BY sxh

select * from xt_gnb
where fid = #{fid,jdbcType=VARCHAR}
and dyqx  >=  #{dyqx,jdbcType=INTEGER}
and zt = #{zt,jdbcType=INTEGER}
ORDER BY sxh
